import { useState, useEffect } from 'react';
import axios from 'axios';
import TodoItem from './components/TodoItem';
import './index.css';

function App() {
  const [task, setTask] = useState('');
  const [todos, setTodos] = useState([]);

  const fetchTodos = async () => {
    const res = await axios.get('http://localhost:5000/api/todos');
    setTodos(res.data);
  };

  const addTodo = async () => {
    if (task.trim()) {
      const res = await axios.post('http://localhost:5000/api/todos', {
        task,
        completed: false,
      });
      setTodos([res.data, ...todos]);
      setTask('');
    }
  };

  const toggleComplete = async (id, completed) => {
    const res = await axios.put(`http://localhost:5000/api/todos/${id}`, {
      completed: !completed,
    });
    setTodos(todos.map(todo => (todo._id === id ? res.data : todo)));
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`);
    setTodos(todos.filter(todo => todo._id !== id));
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-500 via-purple-600 to-indigo-700 p-10 text-white">
      <h1 className="text-4xl font-bold mb-6">ðŸ§  ProTask - Smart ToDo</h1>
      <div className="flex mb-6">
        <input
          className="w-full p-3 rounded text-black"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Create your next great task..."
        />
        <button
          onClick={addTodo}
          className="ml-3 px-6 py-2 bg-green-600 hover:bg-green-700 rounded"
        >
          âž• Add
        </button>
      </div>
      <ul>
        {todos.map(todo => (
          <TodoItem
            key={todo._id}
            todo={todo}
            toggleComplete={toggleComplete}
            deleteTodo={deleteTodo}
          />
        ))}
      </ul>
    </div>
  );
}

export default App;
