import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [task, setTask] = useState('');
  const [todos, setTodos] = useState([]);

  const fetchTodos = async () => {
    const res = await axios.get('http://localhost:5000/api/todos');
    setTodos(res.data);
  };

  const addTodo = async () => {
    if (task.trim()) {
      const res = await axios.post('http://localhost:5000/api/todos', {
        task,
        completed: false,
      });
      setTodos([res.data, ...todos]);
      setTask('');
    }
  };

  const toggleComplete = async (id, completed) => {
    const res = await axios.put(`http://localhost:5000/api/todos/${id}`, {
      completed: !completed,
    });
    setTodos(todos.map(todo => (todo._id === id ? res.data : todo)));
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`);
    setTodos(todos.filter(todo => todo._id !== id));
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-indigo-600 p-10 text-white">
      <h1 className="text-4xl font-bold mb-6">üåü Devendhar's Creative ToDo App</h1>
      <div className="flex mb-6">
        <input
          className="w-full p-3 rounded text-black"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="What‚Äôs your next task?"
        />
        <button
          onClick={addTodo}
          className="ml-3 px-6 py-2 bg-green-600 hover:bg-green-700 rounded"
        >
          Add
        </button>
      </div>
      <ul>
        {todos.map(todo => (
          <li
            key={todo._id}
            className={`p-4 my-2 rounded flex justify-between items-center bg-white text-black shadow-md ${
              todo.completed ? 'line-through bg-gray-200' : ''
            }`}
          >
            <span
              onClick={() => toggleComplete(todo._id, todo.completed)}
              className="cursor-pointer"
            >
              {todo.task}
            </span>
            <button
              onClick={() => deleteTodo(todo._id)}
              className="text-red-600 hover:text-red-800"
            >
              ‚ùå
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
